#include "Data/Monster.h"

using namespace data;
using namespace std;

/* initialize static variables */
int Monster::_saiph_difficulty_min = 0;
int Monster::_saiph_difficulty_max = 0;
int Monster::_monster_symbol_mapping[UCHAR_MAX + 1][INVERSE_BOLD_WHITE + 1] = {
	{0}
};
vector<const Monster*> Monster::_monsters;
map<const string, int> Monster::_monster_name_mapping;

/* protected constructors */
Monster::Monster(
	const string& name,
	const unsigned char& symbol,
	int difficulty,
	int move_rate,
	int ac,
	int magic_resistance,
	int alignment,
	int geno_flags,
	const Attack& a0,
	const Attack& a1,
	const Attack& a2,
	const Attack& a3,
	const Attack& a4,
	const Attack& a5,
	int weight,
	int nutrition,
	int extension,
	int sounds,
	int size,
	int resistances,
	int resistances_conferred,
	int m1,
	int m2,
	int m3,
	int color) : _name(name),
		_symbol(symbol),
		_difficulty(difficulty),
		_move_rate(move_rate),
		_ac(ac),
		_magic_resistance(magic_resistance),
		_alignment(alignment),
		_geno_flags(geno_flags),
		_weight(weight),
		_nutrition(nutrition),
		_extension(extension),
		_sounds(sounds),
		_size(size),
		_resistances(resistances),
		_resistances_conferred(resistances_conferred),
		_m1(m1),
		_m2(m2),
		_m3(m3),
		_color(color),
		_ignores_elbereth(
			(symbol == S_HUMAN
			|| symbol == S_ANGEL
			|| name == "minotaur"
			|| name == "Death"
			|| name == "Pestilence"
			|| name == "Famine")
		) {
	_attacks.push_back(a0);
	_attacks.push_back(a1);
	_attacks.push_back(a2);
	_attacks.push_back(a3);
	_attacks.push_back(a4);
	_attacks.push_back(a5);

	/* calculate the "saiph difficulty" */
	_saiph_difficulty = 0;
	if (_ignores_elbereth)
		_saiph_difficulty += 100;
	_saiph_difficulty += _move_rate * 2; // faster monsters are more dangerous
	_saiph_difficulty -= _ac * 2; // the lower ac the monster got, the more dangerous it is
	_saiph_difficulty += _magic_resistance; // higher magic resistance is bad
	_saiph_difficulty += (a0.maxDamage() + a1.maxDamage() + a2.maxDamage() + a3.maxDamage() + a4.maxDamage() + a5.maxDamage()) * 3; // hard-hitting monsters are mean
	if (m2 & M2_MAGIC)
		_saiph_difficulty += 25; // magic users can be dangerous
	if (m3 & M3_COVETOUS)
		_saiph_difficulty += 50; // covetous!
	if (m2 & M2_NASTY)
		_saiph_difficulty += 50; // nasty!
	if (m2 & M2_STRONG)
		_saiph_difficulty += 25;
	/* should look at elemental attacks, resistances, etc */
	if (_saiph_difficulty < _saiph_difficulty_min)
		_saiph_difficulty_min = _saiph_difficulty;
	if (_saiph_difficulty > _saiph_difficulty_max)
		_saiph_difficulty_max = _saiph_difficulty;
}

