const Monster* Monster::monster(const unsigned char& symbol, int color) {
	if (color < 0 || color > INVERSE_BOLD_WHITE)
		return NULL;
	return monster(_monster_symbol_mapping[symbol][color]);
}

const Monster* Monster::monster(const string& name) {
	map<const string, int>::iterator m = _monster_name_mapping.find(name);
	if (m == _monster_name_mapping.end())
		return NULL;
	return monster(m->second);
}

